ke.which : event.keyCode;    if (eventChooser==13) {         return false;    }  } document.onkeypress = getKey;   </script> <script language="javascript" type="text/javascript">function NuevoAjax(){        var xmlhttp=false;        try	{                xmlhttp = new ActiveXObject("Msxml2.XMLHTTP");        }	catch(e)	{                try		{                        xmlhttp = new ActiveXObject("Microsoft.XMLHTTP");                }		catch(E)		{                        xmlhttp = false;                }        }        if(!xmlhttp && typeof XMLHttpRequest!='undefined')	{                xmlhttp = new XMLHttpRequest();        }        return xmlhttp;}function Cargar(url){        var contenido, preloader;        contenido = document.getElementById('contenido');        preloader = document.getElementById('preloader');        //creamos el objeto XMLHttpRequest        ajax=NuevoAjax();        //peticionamos los datos, le damos la url enviada desde el link        ajax.open("GET", url,true);	//prototipo que sirve para tratar la respuesta:	String.prototype.tratarResponseText = function() {				var pat=/<script[^>]*>([\S\s]*?)<\/script[^>]*>/ig;		var pat2=/\b\s+src=[^>\s]+\b/g;		var elementos = this.match(pat) || [];				for (i = 0; i < elementos.length; i++) {						var nuevoScript = document.createElement('script');			nuevoScript.type = 'text/javascript';			var tienesrc=elementos[i].match(pat2) || [];						if (tienesrc.length) {				nuevoScript.src=tienesrc[0].split("'").join('').split('"').join('').split('src=').join('').split(' ').join('');			}			else {								var elemento = elementos[i].replace(pat,'$1');				nuevoScript.text = elemento;							}						document.getElementsByTagName('body')[0].appendChild(nuevoScript);					}				return this.replace(pat,'');	}         ajax.onreadystatechange=function()	{                if(ajax.readyState==1)		{                        preloader.innerHTML = ".";                        //modificamos el estilo de la div, mostrando una imagen de fondo                        preloader.style.background = "url('ajax-loader-2.gif') no-repeat";                }		else if(ajax.readyState==4)		{                        if(ajax.status==200)			{                                //mostramos los datos dentro de la div                                contenido.innerHTML = ajax.responseText;                                preloader.innerHTML = "";                                preloader.style.background = "url('loaded.gif') no-repeat";                        }			else if(ajax.status==404)			{                                preloader.innerHTML = "La pagina no existe";                        }			else			{                                //mostramos el posible error                                preloader.innerHTML = "Error:".ajax.status;                        }                }        }        ajax.send(null);}</script>	<script type="text/javascript" src="http://ajax.googleapis.com/ajax/libs/jquery/1.4.2/jquery.min.js"></script>	<script type="text/javascript" src="../ddaccordion.js">	</script>	<script type="text/javascript">	//Initialize Arrow Side Menu:	ddaccordion.init({		headerclass: "menuheaders", //Shared CSS class name of headers group		contentclass: "menucontents", //Shared CSS class name of contents group		revealtype: "clickgo", //Reveal content when user clicks or onmouseover the header? Valid value: "click", or "mouseover"		mouseoverdelay: 200, //if revealtype="mouseover", set delay in milliseconds before header expands onMouseover		collapseprev: true, //Collapse previous content (so only one open at any time)? true/false 		defaultexpanded: [0], //index of content(s) open by default [index1, index2, etc]. [] denotes no content.		onemustopen: false, //Specify whether at least one header sho